<?php

declare(strict_types=1);

namespace Tests\Validation\Format;

/**
 * Class Base64Test
 * "data:{{file_type:image|pdf}}/{{file_extension:png|jpg|jpeg|gif}};base64,{{file_encode_in_base64}}
 * @package Tests\Validation\Format
 */
class Base64Test extends \PHPUnit\Framework\TestCase {

    public function get_data_png(): array
    {
        return [
//            [
//                '1' => 'data:image/jpeg;base64,',
//                'expected' => TRUE
//            ],
//            [
//                '2' => 'data:image/jpeg2;base64,',
//                'expected' => FALSE
//            ],
            [
                '3' => 'data:image/jpeg;base64,',
                'expected' => TRUE
            ]
        ];
    }

    /**
     * @dataProvider get_data_png
     * @param $data
     * @param bool $expected
     */
    public function testPNG($data, bool $expected): void
    {
        $is_base64 = $this->is_base64_format($data);
        $expected ? $this->assertTrue($is_base64) : $this->assertFalse($is_base64);
    }

    public function get_data_jpg(): array
    {
        return [
            ['1' => '', 'expected' => TRUE],
            ['2' => '', 'expected' => FALSE]
        ];
    }

    /**
     * @dataProvider get_data_jpg
     * @param $data
     * @param bool $expected
     */
    public function testJPG($data, bool $expected): void
    {
        
    }

    public function get_data_gif(): array
    {
        return [
            ['1' => '', 'expected' => TRUE],
            ['2' => '', 'expected' => FALSE]
        ];
    }

    /**
     * @dataProvider get_data_gif
     * @param $data
     * @param bool $expected
     */
    public function testGIF($data, bool $expected): void
    {
        
    }

    private function is_base64_format($data): bool
    {
        $type = '';

        if (!$data) {
            // data cannot be empty
            return FALSE;
        }

        // preg_match('/^data:application\/(\w+)-base64,/', $data, $type)
        if (preg_match('/^data:image\/(\w+);base64,\w+/', $data, $type)) {

            $data = substr($data, strpos($data, ',') + 1);

            // type to lowercase
            $type = isset($type[1]) && $type[1] ? strtolower(htmlspecialchars(strip_tags($type[1]))) : '';

            if (!$type || !in_array($type, ['jpg', 'jpeg', 'gif', 'png'])) {
                // InvalidFileFormatException
                return FALSE;
            }

            if (!$data) {
                // InvalidBase64DataException
                return FALSE;
            }

            $data_encoded = str_replace(' ', '+', $data);
            $data_decoded = base64_decode($data_encoded);

            if ($data_decoded == FALSE) {
                // InvalidBase64Format
                return FALSE;
            }
        }

        return TRUE;
    }
}
